INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_101_1", /*message*/"　夕焼けに見とれるあなたの足元へ、何故かみかんが転がってくる。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_101_2", /*message*/"　みかん拾いの翌日。拾うのを手伝ってくれたお礼に、と千歌は……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_101_3", /*message*/"　千歌の部屋に招かれたあなたは、部屋の中であるものを見つける。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_101_4", /*message*/"　開いた作詞ノートからは千歌の苦労とAqoursへの愛がありありと伝わってきた。感動を覚えるあなたに千歌は……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_101_5", /*message*/"　千歌の歌詞には愛があふれている。あなたの励ましで、作詞に意欲が湧いてきた千歌。ライブ当日、果たして曲は完成したのだろうか。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_102_1", /*message*/"　ピアノコンサートのポスターに興味を惹かれ、ホールを訪れたあなた。チケットを買い会場に入ると、そこで思いがけない人物と遭遇する。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_102_2", /*message*/"　コンサートに出演するピアニストは、なんと梨子がかつてピアノを習っていた人物だった。懐かしそうな梨子だが、どこか思いつめているようでもあり……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_102_3", /*message*/"　沼津で梨子と再会したあなた。あなたは、梨子に先生とどんな話をしたのかたずねる。梨子が話したのは内浦での生活のこと、そして……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_102_4", /*message*/"　Aqoursのために良い曲を作るため、日々悩み試行錯誤していた梨子。なかなか納得いく成果をあげられないという彼女に、あなたはあるお願いをする。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_102_5", /*message*/"　梨子のピアノを聴きたい、と２人で訪れた浦女の音楽室で、あなたがリクエストした曲は……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_103_1", /*message*/"　淡島に初めてやって来たあなた。どんなものに、どんな人に出会えるのだろう？　期待に胸を膨らませ向かったのは……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_103_2", /*message*/"　果南のダイビングの話に心を惹かれたあなた。果南の手を借り、海に潜ってみることに！");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_103_3", /*message*/"　果南と一緒に内浦の海に潜ったあなた。その美しさを大勢の人に知ってほしいのだが……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_103_4", /*message*/"　あなたと果南、そして鞠莉も加わり、内浦の海の良さを広めるための作戦会議を開始！");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_103_5", /*message*/"　内浦のダイビングスポットをアピールするために一肌脱ぐという鞠莉。手始めは……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_104_1", /*message*/"　あなたは、学校の美化活動の一環でゴミ拾いをしているダイヤと出会う。生徒会長として他の生徒達に気を配るダイヤ。スクールアイドルとは違うその姿に、あなたは新鮮さを感じた。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_104_2", /*message*/"　ルビィと浦女の部室で雑誌を読んでいたあなた。そこに、不思議な音色が聞こえてきた。音は校内の和室から聞こえていて……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_104_3", /*message*/"　ダイヤのいた和室は華道部の部室だった。華道部からも一目置かれているダイヤに誘われ、あなたとルビィは生け花に挑戦することに。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_104_4", /*message*/"　花丸と約束があるらしいルビィと別れ、あなたとダイヤは部室へ移動。ルビィと雑誌を読んでいたことを話したあなたは、ふとダイヤもアイドル雑誌を読むのか気になって……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_104_5", /*message*/"　夕方、帰りがけにあなたはダイヤに尋ねる。やることをたくさん抱えていて大変ではないのか、と。その問いにダイヤは……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_105_1", /*message*/"　沼津へ遊びにやって来たあなた。駅を出たところで偶然曜に声を掛けられ、一緒に沼津の町を回ることに。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_105_2", /*message*/"　まずやって来たのは駅前のショッピングセンター。中のゲームセンターでクレーンゲームに挑戦！");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_105_3", /*message*/"　ケーキを食べながら次の行き先を相談するあなたと曜。水族館か、城跡か？　楽しい1日は始まったばかり。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_105_4", /*message*/"　大きな水門までやってきたあなたと曜。展望スペースからの絶景にテンションが最高潮のあなた。一方の曜はやけに静かになって……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_105_5", /*message*/"　夕方になり、あなたが東京へ帰る時間がやってきた。また一緒に遊ぼうと約束し、あなたは改札をくぐった。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_106_1", /*message*/"　道端で奇妙な物体を拾ったあなた。交番に届けようと思った時、善子に声を掛けられる。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_106_2", /*message*/"　キーホルダーを直してくれたあなたに、善子は感謝の気持ちを込めて、堕天使ヨハネの側近の地位を与える。かくして新たなリトルデーモンが誕生した！");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_106_3", /*message*/"　あなたは、善子から『リトルデーモン増員計画』と称した動画撮影に協力を求められる。撮影にあたり試したいことがあるという善子。一体何をするつもりなのか？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_106_4", /*message*/"　動画撮影決行日。期待と緊張でそわそわするあなたとは対照的に、善子は憮然としている。どうやら動画内で実況するゲームに不満があるようだ。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_106_5", /*message*/"　生配信が始まり、ヨハネモードの善子と共にゲームを開始するあなた。どんな配信内容となるかドキドキしたのもつかの間、善子の絶叫が響き渡る……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_107_1", /*message*/"　散歩の休憩を兼ねて、図書館を訪れたあなた。館内を歩いていると、どこからか聞き覚えのある声が……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_107_2", /*message*/"　花丸の貸出し手続きを待つ間、あなたとルビィは図書館の掲示板を眺めていた。様々なお知らせが張り出されている中に、1つ目を引くものがある。それは……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_107_3", /*message*/"　図書館アンソロジーへの投稿に興味を抱きつつも、なかなか決心がつかない花丸。可能性を潰してほしくないあなたとルビィは、花丸の背中を押す。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_107_4", /*message*/"　アンソロジーへの投稿を決意した花丸。あなたとルビィも応援するが、執筆するためには『ある問題』を乗り越えなければならなくて……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_107_5", /*message*/"　図書館アンソロジーが発行される日、あなた・花丸・ルビィは図書館を訪れた。特設コーナーには、花丸が投稿したアンソロジーが展示してあり……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_108_1", /*message*/"　東京の街を歩いていたあなたは、海外からの観光客から道を聞かれて返答に窮していた。そこに意外な人物が救いの手を差し伸べて……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_108_2", /*message*/"　鞠莉の東京観光に同行することになったあなた。行先を尋ねるが、鞠莉は笑ってはぐらかすばかり。そして到着した先は……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_108_3", /*message*/"　鞠莉のフットワークの軽さに付いて行くので精一杯なあなた。しかも鞠莉の行く先は観光地と言うにはいささか不釣り合いな場所で……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_108_4", /*message*/"　目的地は虹ヶ咲学園！　あなたの案内で校内を見て回る鞠莉。そこで出会ったのは、虹ヶ咲のスクールアイドル・歩夢だった。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_108_5", /*message*/"　そろそろ沼津へ帰ると言う鞠莉に、あなたは今日の東京観光の本当の目的を尋ねる。鞠莉の答えは……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_109_1", /*message*/"　ある日、浦女の部室に忘れ物をしてしまったあなた。急いで取りに戻ったあなたが見たのは、夕暮れの部室で1人作業をするルビィの姿だった。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_109_2", /*message*/"　ルビィを手伝い、衣装の手直しを終えたあなた。これで完成かと思いきや、ルビィはまだ思うところがある様子。一体何が気になっているのだろうか？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_109_3", /*message*/"　ブローチの材料を集めるために、沼津の外の手芸店まで足を延ばしたあなたとルビィ。目当ての材料は見つかるのだろうか？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_109_4", /*message*/"　ブローチ作りスタート！　Aqoursメンバーそれぞれに相応しいものを作るため、ルビィはしっかりとイメージを固めていた。あなたはルビィのAqoursに対する想いの深さを改めて感じ取る。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_109_5", /*message*/"　ついにブローチが完成！　目の前にきらめくオリジナルアクセサリーに感動するあなたとルビィ。しかしあなたはある異変に気付いてしまう。一体何が起こったのか？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_1_1", /*message*/"　μ&apos;sの活動を手伝うことになったあなた。仲間として受け入れてもらえるか気がかりなところだが、部室で待つ穂乃果は……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_1_2", /*message*/"　「同じ方向へ走っていく仲間」、穂乃果はそう言ってあなたのことを迎え入れてくれた。今日もあなたは音ノ木坂学院へ向かって歩き出した……はずが、迷子になってしまった！");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_1_3", /*message*/"　迷子になってしまったあなたがたどり着いたのは、穂乃果の家だった。みんなを元気に引っ張っていってくれる穂乃果だが、そんな穂乃果にも意外な一面があったようで……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_1_4", /*message*/"　大好きなスクールアイドル以外のことには力が入らない様子の穂乃果。でも、そんな一面が穂乃果らしくも思えてきて……穂乃果の大好物を知ったあなたは、一つの提案をする。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_1_5", /*message*/"　部活以外でも仲良くなれたことに、喜びを感じる穂乃果とあなた。作曲中のあなたにも、穂乃果は手を差し伸べてくれて……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_1", /*message*/"　眠気を抑えながら朝食を食べるあなたの家に、幼馴染みの歩夢が訪ねてきた。あなたは歩夢と連れ立って学校へ向かう。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_2", /*message*/"　お昼休みになり、食堂に移動したあなたと歩夢。歩夢は手作りのお弁当を持って来ており、その丁寧で色鮮やかな中身にあなたは感心する。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_3", /*message*/"　帰りの電車の中で、あなたは歩夢と自分が初めて同じ部活に入っていることを知る。それを喜ぶあなたに、歩夢も嬉しそうな表情を浮かべて……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_4", /*message*/"　ある日の放課後、あなたは部室で活動記録を書いていた。歩夢にどんなことを書いているのか尋ねられ、あなたは記録を読み上げる。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_201_5", /*message*/"　ライブイベントの開催日。参加する虹ヶ咲メンバーの中でも、歩夢にとっては初めてのライブだ。良い結果が出ることを願い、あなたは歩夢達をステージに送り出す。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_1", /*message*/"　１年生のかすみは、どうにも他のメンバーにいじられがち。憤慨するかすみだが、愛されている証拠だとあなたは宥める。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_2", /*message*/"　大きなバッグを抱えて部室に現れたかすみ。一体何が入っているのか？かすみは嬉々とした表情でバッグの中身を机に広げる。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_3", /*message*/"　週末に学園でオープンキャンパスが開かれることになった。そこで行われる部活動紹介に、スクールアイドル同好会も参加することに。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_4", /*message*/"　臨時センター投票の締切日。持ち前の可愛らしさと入念な根回しで自信満々のかすみ。センターに選ばれたのは果たして……！？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_202_5", /*message*/"　部活動紹介を無事にやり切った同好会メンバー。いつもの調子で会話に混ざるかすみだが、その心中は複雑なものだった……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_1", /*message*/"　部活の休憩中、しずくと話をするあなた。演劇部と兼部のしずくは、いつ舞台の練習が入ってもいいように演劇の練習も毎日欠かさず行うらしく……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_2", /*message*/"　演劇が大好きなしずくは、自身で劇の設定を作ることも大好きらしい。その能力は幼少期から既に発揮されていたようで……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_3", /*message*/"　ある日、あなたはしずくに劇の練習相手になってほしいと頼まれる。どうやら、役のセリフの中に上手く言えないものがあるようで……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_4", /*message*/"　探し物をしに部室を訪れたあなたは、中で奇怪な動きをしているしずくと遭遇する。一体彼女の身に何が起こったのか！？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_203_5", /*message*/"　しずくのために、同好会メンバーでしずくのバレーの練習相手になることに。しずくは苦手な球技を克服することが出来るのだろうか？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_1", /*message*/"　いつも努力を怠らず、ストイックな果林。ある日、部室に戻ったあなたは、居残りしている果林を見つける。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_2", /*message*/"　果林があなたを探していた、と声をかけてきた。その目的は、デートのお誘い……！？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_3", /*message*/"　毎日のように、放課後居残り練習をしている果林を心配するあなただったが、果林の思いを聞いて……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_4", /*message*/"　スクールアイドルを始めて、毎日が充実しているという果林に、一層サポートを頑張ろうと思うあなた。ある日、果林からパフォーマンスについて相談がしたいことがあると言われ……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_204_5", /*message*/"　自分の良さを生かすためにはどうすればいいか悩んでいた果林だが、同好会メンバーから意見を聞き一歩踏み出した。ただ練習を続けるうちに、少し行き詰ってしまったようで……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_1", /*message*/"　明るくて、誰とでも仲良くなれる愛。そんな愛は今日も人気者のようで、次々と声を掛けられ……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_2", /*message*/"　先輩後輩問わず、友達がとても多い愛。そんな愛が自分の友達を誘い、同好会お披露目カラオケを開催することに！？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_3", /*message*/"　愛の周りは賑やかで、自然と人が集まる。ある日、あなたは愛と璃奈と一緒にお昼を食べることになった。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_4", /*message*/"　愛はおばあちゃんっ子であると同時に、お姉ちゃんっ子だった。そして愛の人懐っこさは二人から来ているのかも……と思うあなた。そのお姉ちゃんの受け売りである、「うぇいうぇいの関係」とは……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_205_5", /*message*/"　スクールアイドルとしてみんなを笑顔にしたい、と思う愛。いざ愛のライブ当日、パフォーマンスはどうだったのか……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_1", /*message*/"　いつものように部活を始めようとする同好会メンバー。しかし、彼方が見当たらない。仕方なくあなたが探しに行くと、彼方は保健室で眠っていた……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_2", /*message*/"　部活に遅れないよう彼方に釘を刺すため、彼方のクラスを訪ねたあなた。しかし、彼方は見当たらない。どこに行ってしまったのだろう？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_3", /*message*/"　部活中、作詞に勤しむあなたの元に彼方がやって来る。妹の遥が大好きな彼方は、あなたに妹への愛を語り出す。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_4", /*message*/"　お昼休み、学食で彼方に話しかけられたあなた。お弁当を作り過ぎたから、あなたに食べてほしいのだと言う。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_206_5", /*message*/"　部活が終わって熟睡してしまった彼方。全く起きる気配が無く、同好会メンバーは心配する。あなたは、以前彼方が話してくれた『眠い理由』を思い出し……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_1", /*message*/"　スクールアイドル”優木せつ菜”として、また生徒会長の”中川菜々”として日々生活するせつ菜。そんなせつ菜の姿を見ているあなたは……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_2", /*message*/"　”せつ菜”としても”菜々”としても頑張るせつ菜を尊敬するあなた。ただ大変なこともやはりあるようで、ある日せつ菜から部活を休むという連絡がきた。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_3", /*message*/"　せつ菜はスクールアイドル活動を両親に内緒にしながら行っていた。そんなせつ菜がスクールアイドルと同じくらい「大好き」なもう一つのものが……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_4", /*message*/"　アニメや漫画が大好きなせつ菜。そこであなたはせつ菜が読みたがっていた漫画が揃う自宅に招待することに。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_207_5", /*message*/"　アニメや漫画と同じように、せつ菜の家ではスクールアイドルも禁止だった。そこで”優木せつ菜”と名前を変えてスクールアイドルをやっていたのだという。せつ菜のスクールアイドルに対する熱い思いを知ったあなたは、しずくと璃奈と一緒に生徒会の仕事を手伝うことに。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_1", /*message*/"　スクールアイドルとして日々努力しているエマ。そんなエマがスイスから日本に来た理由とは……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_2", /*message*/"　子供のころに動画で見たスクールアイドルに憧れて、自分もスクールアイドルになるために日本に留学してきたというエマ。エマにスイスでの生活を聞いてみると……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_3", /*message*/"　いつかスイスでもライブをしたいと約束し合ったエマとあなた。ある日、同好会メンバーで、自分はどういうスクールアイドルになりたいかという会話になった。その時エマは……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_4", /*message*/"　スクールアイドルの活動を通して、自分がどういうスクールアイドルになりたいか探していくことになったエマ。そんな中、エマの部屋で次のイベントに向けての相談会をすることになった。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_208_5", /*message*/"　エマの姿を見て、優しく包み込んでくれるようなスクールアイドルになれるのではないか、と感じたあなた。ある相談をエマにすることに。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_1", /*message*/"　あなたと愛が話しているところに、璃奈がやって来る。ボードの表情を変える前に璃奈の感情を言い当てたあなたに、璃奈は驚く。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_2", /*message*/"　璃奈ちゃんボードのおかげで、周囲の人との繋がりを持てるようになった璃奈。璃奈は、もっとたくさんの人と繋がりたいと言い……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_3", /*message*/"　璃奈を誘い、スクールアイドルのライブへやって来たあなた。大勢の人がスクールアイドルのために集まっている光景に、璃奈はとても驚いて……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_4", /*message*/"　ライブでたくさんの人達と心を繋げるため、日々練習に励む璃奈。しかし、ライブをやる上で絶対に無視出来ない問題が発覚する。それは……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_209_5", /*message*/"　璃奈の初ライブの日。『新装備』を携えた璃奈はステージに臨む。会場に来た人達と心が繋がることの願いながら……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_2_1", /*message*/"　歌と踊りだけではステージは出来上がらない！　その裏には……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_2_2", /*message*/"　音ノ木坂学院の生徒会会長でもある絵里。あなたも生徒会を見学することに……って、見学じゃない！？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_2_3", /*message*/"　絵里のおかげで、部活のサポートも上々。安心したあなたが聞いたものは……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_2_4", /*message*/"　夜の学校に響く絵里の悲鳴。忍び寄るのは恐怖か、それとも……？？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_2_5", /*message*/"　絵里の魅力にもふれて、毎日の部活はどんどん充実していきます♪");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_3_1", /*message*/"　μ&apos;sの活動を手伝うことになったあなたは、スクールアイドルになる前からずっと仲良しだった穂乃果、海未、ことりの三人に話を聞くことに。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_3_2", /*message*/"　普段は穂乃果や海未たちを優しくサポートしていることり。それでも、ことりには絶対妥協できないコダワリがあったようで……！");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_3_3", /*message*/"　μ&apos;sの衣装作りを担当していることり。ことりのこだわりを知ったあなたは、街でことりにばったり出会い……って、ことりちゃんじゃないの！？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_3_4", /*message*/"　メイドカフェでミナリンスキーとしてバイトをしていたことり。μ&apos;sとメイドさんを両立していることりだが、衣装のことで悩んでいる様子で……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_3_5", /*message*/"　衣装のことで悩んでいたことりを、博物館へ連れ出したあなた。困ったときに助けてくれるあなたの存在を、ことりは心強く思っているようで……♪");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_4_1", /*message*/"　μ&apos;sの手伝いをすることになったあなたは、海未にスクールアイドルへの想いを聞くことに。海未にとってのスクールアイドルとは？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_4_2", /*message*/"　スクールアイドル。それは穂乃果にとっての夢であり、海未にとっての夢でもあった。海未がμ&apos;sの歌詞をほとんど手がけていると聞いたあなたは……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_4_3", /*message*/"　海未の家で、作詞風景を見学させてもらうことになったあなた。海未が心を込めて作詞をする中、あなたは……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_4_4", /*message*/"　海未が感情豊かに作詞をする姿を見たあなたは、海未に惹かれていくことを実感する。ある日、海未の弓道大会についていくことになり……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_4_5", /*message*/"　μ&apos;sの活動以外でも見えてくる、海未の色々な魅力。これからもお互いをもっと知っていきたいと望む二人は……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_5_1", /*message*/"　元気いっぱいの凛。真姫と花陽と一緒にごあいさつ♪");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_5_2", /*message*/"　花陽といつも仲良しの凛。何か手伝えることはないかと聞くあなたですが……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_5_3", /*message*/"　花陽の代わりに、凛を応援することになったあなたは……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_5_4", /*message*/"　花陽のおにぎりを食べてうれしそうな凛。でもなんだか恥ずかしそうで……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_5_5", /*message*/"　あなたに応援されてラーメンの味もわからなくなるほど嬉しい凛は……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_6_1", /*message*/"　μ&apos;sの活動を手伝うことになったあなたは、μ’sの作曲を担当する真姫と話をすることに。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_6_2", /*message*/"　あなたに手伝ってもらう必要性を感じない、と話した真姫。それでも、あなたのことを拒絶したかったわけではないようで……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_6_3", /*message*/"　これから仲良くなれたら、と伝えてくれた真姫は不器用ながらも優しい子。そんな彼女とあなたが音楽室で出会い……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_6_4", /*message*/"　ピアノを弾きながら互いの話をした二人は、ピアノを弾く理由もなんだか共感出来て。あなたが作曲しているところに、真姫が通りがかり……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_6_5", /*message*/"　作曲の才能がある、と真姫にほめられたあなた。μ’sの曲にも、真姫の言葉にも、強く励まされていることを実感して……♪");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_7_1", /*message*/"　面白い子が来た、と喜ぶ希。でも、それって……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_7_2", /*message*/"　生徒会の副会長としても活動する希。希の学校生活に対する想いにふれることに。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_7_3", /*message*/"　希お得意の占いは……ま、まさかの結果に！");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_7_4", /*message*/"　わるーい占いの結果。希のアドバイスで、このピンチを切り抜けたい！");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_7_5", /*message*/"　希の言葉に導かれ、赤点を取らないよう行動することにしたあなたは……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_8_1", /*message*/"　花陽が何よりも大好きなもの、それはもちろん……！");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_8_2", /*message*/"　引っ込み思案だった花陽の手を引いてくれたのは、凛だった。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_8_3", /*message*/"　朝の秋葉原を歩いていたあなたは花陽を見かけるが、様子がいつもと違うみたい！");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_8_4", /*message*/"　今日の花陽はなんだか嬉しそう。その理由は……？");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_8_5", /*message*/"　一緒におでかけして花陽との絆の少しずつ強くなってきた。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_9_1", /*message*/"　μ&apos;sの部活を手伝うことになったあなた。１人だけそのことに不満そうなメンバーが……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_9_2", /*message*/"　にこに本気の気持ちを伝えたいあなた。にこは疑念を隠せない様子だが……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_9_3", /*message*/"　期待に応えられるよう、努力するあなた。その熱意はにこにもだんだん伝わって……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_9_4", /*message*/"　スクールアイドルが好きなのに、どうしてスクールアイドルにならないの？　と訊くにこにあなたは……。");
INSERT INTO `m_dictionary` VALUES (/*id*/"member_story_description_9_5", /*message*/"　にこに認められたいあなた。特訓の成果を発揮するときがきた！");
