CREATE TABLE m_live_difficulty_gimmick(
  id INTEGER NOT NULL,
  live_difficulty_master_id INTEGER NOT NULL,
  trigger_type INTEGER NOT NULL,
  condition_master_id1 INTEGER NOT NULL,
  condition_master_id2 INTEGER,
  skill_master_id INTEGER NOT NULL,
  name TEXT NOT NULL,
  description TEXT NOT NULL,
  PRIMARY KEY (id),
  FOREIGN KEY (live_difficulty_master_id) REFERENCES m_live_difficulty(live_difficulty_id),
  FOREIGN KEY (condition_master_id1) REFERENCES m_skill_condition(id),
  FOREIGN KEY (condition_master_id2) REFERENCES m_skill_condition(id),
  FOREIGN KEY (skill_master_id) REFERENCES m_skill(id)
);
